CREATE DATABASE IF NOT EXISTS realest_reviews_test;
USE realest_reviews_test;

-- Tabelle media erstellen
CREATE TABLE IF NOT EXISTS media (
    id INT PRIMARY KEY AUTO_INCREMENT,
    title VARCHAR(255) NOT NULL,
    type VARCHAR(64) NOT NULL,
    creator VARCHAR(255),
    release_date DATE,
    image_url TEXT
);

-- Tabelle reviews erstellen
CREATE TABLE IF NOT EXISTS reviews (
    id INT PRIMARY KEY AUTO_INCREMENT,
    media_id INT NOT NULL,
    username VARCHAR(64) NOT NULL,
    review_text TEXT,
    overall_score DECIMAL(3,1),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    keywords TEXT,
    FOREIGN KEY (media_id) REFERENCES media(id)
);

-- Tabelle review_categories erstellen
CREATE TABLE IF NOT EXISTS review_categories (
    id INT PRIMARY KEY AUTO_INCREMENT,
    review_id INT NOT NULL,
    category VARCHAR(64) NOT NULL,
    score INT NOT NULL CHECK (score BETWEEN 1 AND 10),
    FOREIGN KEY (review_id) REFERENCES reviews(id)
);

-- Tabelle review_votes erstellen
CREATE TABLE IF NOT EXISTS review_votes (
    id INT PRIMARY KEY AUTO_INCREMENT,
    review_id INT NOT NULL,
    username VARCHAR(64) NOT NULL,
    vote ENUM('helpful', 'funny', 'unhelpful') NOT NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (review_id) REFERENCES reviews(id)
);

